const  mapStateToProps = state =>{
  
    return{
	  modal: state.showModal,
	  eventUpIn : state.eventUpdate_Insert,
	  form : state.formroom,
	  formtable : state.formtable,
	  event : state.event
    }
  }

const mapDispatchToProps = (dispatch, props) =>{
  return {
	onInsertRoom: (data, status) =>{
		dispatch(action. acInsertRoomsRequest(data, status))
	},
	upDateTable : (data) =>{
		dispatch(action.acUpdateTableRequest(data))
	},
	inSertTable : (data) =>{
		dispatch(action.acInsertTableRequest(data))
	},
	OntapRoom : (tap) =>{
		dispatch(tap)
	},
	showAlert : (id) =>{
		dispatch(id)
	}
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(Content);
