import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as action from '../../actions/index';





class SelectRoom extends Component {
 

  ListRoom = (listArray) =>{
    console.log(listArray.length)
    var result  = null;
    if(listArray.length > 0){
      result = listArray.map((room, index) =>{
        console.log(room);
        return (
        <option value={room.IdArea}>{room.BranchName}</option>
        )
      })
    }
    return result;
  }
  render() 
  
  {
    //   var {data, index} = this.props
      console.log('da vao selectdata');
    console.log(this.props);

    return (
        <select name="" value = '1'id="input" onChange = {()=>{}} class="form-control" disabled>
        <option value="">Tất cả</option>
        {this.ListRoom(this.props.table)}

    </select>
       
    )
    

}
}
const  mapStateToProps = state =>{
  console.log(state);

  return{
    table : state.table
  }
};

const mapDispatchToProps = (dispatch, props) =>{
  return {
	onFetchsttTable : () =>{
	  dispatch(action.acFetchsttTableRequest())
	},
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(SelectRoom);
