      {
     return (
 
       <ListDataofRoom
       index = {index+1}
       data = {data}
       
       >

       </ListDataofRoom>
 
      
       );
      })
    }
    else
    {
      return (
        <Emty/>
      )
    }
   return result
    };
  callbackFunction = (data) => 
  {
  
    this.setState({
      id : data.id,
      idarea : data.IdArea,
      name : data.BranchName,
      onTap : 'modal fade show-modal'
    })
  }
