import * as Types from './../constants/ActionType';
import callApi from './../ultis/apiCaller';
export const acFetchProductsRequest = () => {
   
    return next =>{
       
         callApi('getdata', 'GET', null).then(res =>{
             console.log('5');
           
           next(acFetchProducts(res.data));
          
        });

    };
}
export const acInsertProductsRequest = (data) => {
    const status  = {};
    return next =>{
       
         callApi('insertdata', 'POST', data).then(res =>{
           if(res.status = 200){

             status = {
                status : 'success',
                classcomponent : 'alert alert-success',
                text : 'Thêm mới dữ liệu thành công ^-^'
            }
            window.$('#modal-id').modal('toggle'); 

            next(acInsertProducts(res.data, status));
           }else{
            status = {
                status : 'error',
                classcomponent : 'alert alert-danger',
                text : 'Thêm mới dữ liệu thất bại ! (T-T)'
            }
            window.$('#modal-id').modal('toggle'); 

            next(acInsertProducts([{}], status));
           }

           
          
        }).catch((err) =>{console.log(err)});

    };
}
export const acInsertProducts = (product, status) =>{
    
    return {
        type : Types.INSERT_PRODUCT,
        product,
        status
    }
}
export const acFetchProducts = (product) =>{
    
    return {
        type : Types.FETCH_PRODUCTS,
        product
    }
}
export const acDeleteProductsRequest = (id) => {
   
    return next =>{
       
         callApi('deletedata', 'POST', id).then(res =>{
             console.log(res.data);
           
           next(actDeleteProduct(res.data));
          
        });

    };
}
export const actDeleteProduct = (product) =>{
    return {
        type : Types.DELETE_PRODUCT,
        product
    }
}
export const acUpdateProductsRequest = (table) =>{

    return next =>{
       
        callApi('updatedata', 'POST', table).then(res =>{
            console.log(res);
          
          next(actUpdateProduct(res.data));
         
       }).catch((err) => {console.log(table)})

   };

}
export const actUpdateProduct = (product) =>{
    return {
        type : Types.UPDATE_PRODUCT,
        product
    }
}
