import React, { Component } from 'react';
import {Route, Link} from 'react-router-dom';
import './../../app.css';



class Menu extends Component {
 
  menu = [
    {
      name : 'Tổng quan',
      to : '/',
      exact : true,
    },
    {
      name : 'Hóa đơn',
      to : '/order',
      exact : false
    },
    {
        name : 'Bán hàng',
        to : '/pos',
        exact : false
      },
      {
        name : 'Phòng / Bàn',
        to : '/table',
        exact : false
      },
      {
        name : 'Khách hàng',
        to : '/customer',
        exact : false
      },
      {
        name : 'Thực đơn',
        to : '/product',
        exact : false
      },
      {
        name : 'Kho',
        to : '/warehosing',
        exact : false
      }
  ];
  
   MenuLink = (label, to, activeOnlyWhenExact)=>{
    return (
      <Route
      
      path =  {to}
      exact = {activeOnlyWhenExact}
      children = {({match}) =>
      {
        var active  = match ? 'active' : '';
        c
        return (
          <li class = {active}>
            <Link to = {to}>
              {label}
            </Link>
          </li>
        );
      }}
      />
     
    )
  }
  
  showMenus = (menu) =>
  {
      var result = null;
      if(menu.length > 0)
      {
        result = menu.map((menu, index) =>
        {
          return (
           this.MenuLink(menu.name, menu.to,menu.exact, index)
          );
        });
      }
      return result;
  }
  render() {
    return (
     
       
      <div class="row">
      <div class="col-md-12">
          <div class="sidebar sidebar-fixed" >
              <ul class="list-group">
              {this.showMenus(this.menu)}
                    </ul>
          </div>
      </div>
  
  </div>
     
    );
  }
}

export default Menu;
