import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import * as action from '../../actions/index';

import Modal from './Modal'
import ListData from './ListData';





class ListTable extends Component {
  

  // shouldComponentUpdate(nextProps, nextState){
  //   return true
  // }

  constructor(props){
    super(props);
    this.state = {
      id : '',
      name : '',
    }
    } 
    componentWillMount(){
      this.props.fetchAllProducts();
    }
    showData = (datas) =>
    {
    var result = null;
    if(datas.length > 0)
    {
      
      result = datas.map((data,index) =>
      {
     return (
 
       <ListData
       index = {index}
       data = {data}
       >

       </ListData>
 
       // <ListTable
       // key = {index}
       // product = {table}
       // index = {index}
       
       // />
       );
      })
    }
   return result
    };
  // callbackFunction = (dataofButton) => 
  // {
  //   this.setState({id : dataofButton})
  // }
   // console.log(this.props.history.match.params.id);
  
   // console.log(this.props.history.match.params.id);
 
//  onDelete = (id) =>{
//      this.props.onDeleteProduct(id);
//  }
  render() 
  
  {
      

    return (
     <tbody>
						{this.showData(this.props.products)}


             <Modal state = {this.state}/>
             </tbody>
       
    )
    

}
}



const  mapStateToProps = state =>{
  console.log(state.products);

  return{
    products: state.products
  }
}


const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
    fetchAllProducts : () =>{
      dispatch(action.acFetchProductsRequest());
    },
    onDeleteProduct : (id) =>{
     
      dispatch(action.acDeleteProductsRequest(id));
    }
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(ListTable);
