import React, { Component } from 'react';


import * as action from '../../actions/index';
import {connect} from 'react-redux';





class LiNavigation extends Component {
  

     showActive = (e, index) =>{
        console.log(index);
        
      e.preventDefault()
      props.callbackfunction(index)
    }

  
  render() 
  
  {
    
    return (
        <li onClick = {(e)=>this.showActive(e,props.index)}><a id = {props.id} href="#">{props.index}</a></li>
    
    )
    

}
}
const  mapStateToProps = state =>{
  return{
   status : state.status
  }
}



export default connect(mapStateToProps,null)(LiNavigation);
