// 	var target = event.target;
// 	var name = target.name;
// 	var value = target.value;
// 	this.props.callbackOnchange(name,value);

// }

  render() 
  
  {
      
console.log(this.props);
    return (
						
<div class={this.state.onTapTable}  id="modal-id">
			<div class="modal-dialog margin-modal-table">
				<div class="modal-content">
					<div class="modal-header background-modal">
						<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>
						<h4 class="modal-title">Thêm Dữ Liệu</h4>
					</div>
					<div class="modal-body">
					<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Mã Bàn</b>
                    </div>
                    <div class="col-md-9 format-text">
					  <input type="text" 
					   type="text" 
					   name="id" 
					   value = {this.state.id}
					   onChange = {this.onChange}
					   placeholder="Nhập mã bàn" 
					   class="form-control" />
                    </div>
                </div>
                <div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Tên Bàn</b>
                    </div>
                    <div class="col-md-9">
					  <input 
					  type="text" 
					  name="name" 
					  value = {this.state.name}
					  onChange = {this.onChange}
					  placeholder="Nhập tên bàn"
					  class="form-control"/>
                    </div>
                </div>
				<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Trạng thái</b>
                    </div>
                    <div class="col-md-9">
					<select name="" id="input" class="form-control" required="required">
						 <option value=""></option>
					 </select>
                    </div>
                </div>
				<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Phòng / Tầng</b>
                    </div>
                    <div class="col-md-9">
					<select name="" id="input" class="form-control" required="required">
						 <option value=""></option>
					 </select>
                    </div>
                </div>
				<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Hóa Đơn</b>
                    </div>
                    <div class="col-md-9">
					  <input 
					  type="text" 
					  name="name" 
					  value = {this.state.name}
					  onChange = {this.onChange}
					  placeholder="Hóa Đơn"
					  disabled
					  class="form-control"/>
                    </div>
                </div>
				
					</div>
					<div class="modal-footer">
						<button type="button" class="btn btn-danger" onClick = {() =>{this.setState({onTapTable : 'hide-modal'})}}><i class="fa fa-close" aria-hidden="true"></i>&nbsp;&nbsp;Close</button>
						<button type="button" class="btn btn-primary" onClick = {() => this.submit()}><i class="fa fa-save" aria-hidden="true"></i>&nbsp;&nbsp;Save changes</button>
					</div>
				</div>
			</div>
		</div>
       
    )
    

}
}
const  mapStateToProps = state =>{
	console.log(state.products);
  
	return{
	  products: state.products
	}
  }
const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
   
    onUpdateTable : (id) =>{
     
      dispatch(action.acUpdateProductsRequest(id));
    }
  }
}


export default connect(null,null)(ModalInsert);
