import * as Types from '../../constants/ActionType';

const initialState1 = [{}];


const status = (state = initialState1 , action) =>
{
  let newState1 = [...state];
  console.log('dispatch trong status');
  switch(action.type){ 
    // case Types.FETCH_PRODUCTS:
    //   newState = action.product
    case Types.INSERT_PRODUCT :
        newState1 = action.status;
        return newState1;   
        // case Types.DELETE_PRODUCT :
        //   newState = action.product;
        //   return newState; 
        //   case Types.UPDATE_PRODUCT :
        //     newState = action.product;
        //     return newState;     
      default: return newState1;
  }
}
export default status;
