class ListPagination extends Component {
  
  constructor(props){
    super(props);
    this.state = {
     active : 'pagination-col',
      index : 1,
    }
    } 
    callbackFromShowactive = (data) =>{
      this.setState({index : data}, ()=>{
        console.log(this.state.index);
        this.props.fetchAllTables({index :this.state.index});
      })
    }

    prevPage = (e) =>{
      e.preventDefault()

      if(this.state.index == 1)
      {
        return;
      }
      else
      {
        
        this.setState({index : this.state.index - 1 }, ()=>{
          this.props.fetchAllTables({index :this.state.index-1});
        })
      }
    }
    nextPage = (e) =>{
      e.preventDefault()

      if(this.state.index == this.props.tables)
      {
        return;
      }
      else
      {
        
        this.setState({index : this.state.index + 1 }, ()=>{
          this.props.fetchAllTables({index :this.state.index+1});
        })
      }
    }
  // shouldComponentUpdate(nextProps, nextState){
