    {
        var result = [];
if(number > 0)
{
    for (var i =1; i<= number;i++)
    {
       if(i==this.state.index){

       result[i] = <Li index = {i}
       id = {this.state.active}
       callbackfunction = {this.callbackFromShowactive}
       />
       // result[i] = <li data-id="1"  onClick = {()=>{this.showActive()}}><a id = {this.state.active} href="#">{i}</a></li>
      }
      else
      {
        result[i] = <Li index = {i}
        id = ''
        callbackfunction = {this.callbackFromShowactive}

        />
      // result[i] = <li value={i} onClick = {(e)=>{this.showActive(e,e.target.value)}}><a id ="" href="#">{i}</a></li>
       }
    }
    console.log(result);
    return result;
}

    return result;


     

