import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import {connect} from 'react-redux';
import * as action from './../../actions/index';






class ListTable extends Component {
  
  componentWillMount(){
		this.props.fetchAllProducts();
	}
	
   // console.log(this.props.history.match.params.id);
   showRooms = (tables) =>
   {
 console.log(tables);
	 var result = null;
	 if(tables.length > 0)
	 {
	   
	   result = tables.map((table,index) =>
	   {
		console.log(index);
		return (
			<ListTable
			key = {index}
			product = {table}
			index = {index}
			
			/>
		  );
	   })
	 }
	return result
   };
   // console.log(this.props.history.match.params.id);
 onUpdate = (id) =>{
    console.log(id);
 }
 onDelete = (id) =>{
     this.props.onDeleteProduct(id);
 }
  render() 
  
  {
      	  console.log('da vao listtable')

    var {product , index} = this.props;

    return (
        <tr>
            <td>{product.IdArea}</td>
            <td>{product.BranchName}</td>
            <td>
               
               <button type="button" 
               class="btn btn-primary" onClick = {() => this.onUpdate(product.id)}>Update</button>
               
               <button type="button" 
               class="btn btn-danger margin-button"
               onClick = {() => this.onDelete({id : product.id})}
               >Delete</button>
               
               

            </td>

        </tr>
    )
    

}
}
const  mapStateToProps = state =>{
  console.log(state.products);

  return{
    products: state.products
  }
}


const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
    fetchAllProducts : () =>{
      dispatch(action.acFetchProductsRequest());
    },
    onDeleteProduct : (id) =>{
     
      dispatch(action.acDeleteProductsRequest(id));
    }
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(ListTable);
