import * as Types from './../../constants/ActionType';
import callApi from './../../ultis/apiCaller';

const initialState = () =>
{
  
  const store = () => { callApi('getdata', 'GET', null).then(res =>{
    return res.data
});
console.log(store);
return store;
}
}


const products = (state = initialState(), action) =>
{
  console.log(state);
  let newState = [...state];
  switch(action.type){ 
    case Types.INSERT_PRODUCT :
        newState = action.product;
        return newState;    
      default: return newState;
  }
}
export default products;
