  render() 
  
  {
      
    return (
       <div class="modal-body">
					<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Mã phòng / tầng</b>
                    </div>
                    <div class="col-md-9">
					  <input type="text" 
					   type="text" 
					   name="id" 
					   //value = {this.props.form.room}
					  // onChange = {this.onChange}
					   placeholder="Nhập mã phòng / tầng" 
					   class="form-control" />
                    </div>
                </div>
                <div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Tên phòng / tầng</b>
                    </div>
                    <div class="col-md-9 ">
					  <input 
					  type="text" 
					  name="name" 
					  //value = {this.props.form.name}
					 // onChange = {this.onChange}
					  placeholder="Nhập tên phòng / tầng"
					  class="form-control"/>
                    </div>
                </div>
					</div> 
    )
    

}
}
const  mapStateToProps = state =>{
    console.log(state)
  
    return{
      form : state.formroom
    }
  }

export default connect(mapStateToProps,null)(Modalbody);
