import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import * as action from '../../actions/index';
import {connect} from 'react-redux';
import Modal from './ModRoom';
import ListData from './ListData';

import Emty from './Emty';



class ListTable extends Component {
  

  // shouldComponentUpdate(nextProps, nextState){
  //   return true
  // }

  constructor(props){
    super(props);
    this.state = {
      id : '',
      idarea : '',
      name : '',
      onTap : 'hide-modal',

    }
    } 
    componentWillMount(){
      console.log('willmount');
      this.props.fetchAllRooms();
      console.log('sauwillmount');

    }
    showData = (datas, state) =>
    {
    var result = null;
    if(datas.length > 0)
    {
      
      result = datas.map((data,index) =>
      {
     return (
 
       <ListData
       index = {index+1}
       data = {data}
       callback = {this.callbackFunction}
       >

       </ListData>
 
      
       );
      })
    }
    else
    {
      return (
        <Emty/>
      )
    }
   return result
    };
  callbackFunction = (data) => 
  {
  
    this.setState({
      id : data.id,
      idarea : data.IdArea,
      name : data.BranchName,
      onTap : 'modal fade show-modal'
    })
  }
  callbackfunctionforSubmitupdate = (id, status) =>{
    this.setState({onTap : status}, ()=>{
      this.props.onUpdateTable(id)
    })

  }
  callbackfunctionForonChange = (name, value) =>{
   
    this.setState({
      [name] : value
    })
 
  }
  componentWillReceiveProps(nextProps){
    console.log('receipt');
    this.setState({});
	}
   // console.log(this.props.history.match.params.id);
  
   // console.log(this.props.history.match.params.id);
 
