import React, { Component } from 'react';

import { connect } from 'react-redux';
//import {acFetchTableRequest, acDeleteProductsRequest} from './../../actions/index';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';

import Listroom from './ListTable';
import ListPagination from  './ListPagination'
import * as action from '../../actions/index';



class Table extends Component {
	constructor(props){
		super(props);
		this.state = {
		disabled : '',
		search : 'btn btn-primary size-group',
		undo : 'btn btn-primary size-group hide-modal'
		}
	  }
	  ListStatus = (listArray) =>{
		console.log(listArray.length)
		var result  = null;
		if(listArray.length > 0){
		  result = listArray.map((status, index) =>{
			console.log(status);
			return (
			<option value={status.Id_stt}>{status.name}</option>
			)
		  })
		}
		return result;
	  }
	  ListRoom = (listArray) =>{
		console.log(listArray.length)
		var result  = null;
		if(listArray.length > 0){
		  result = listArray.map((room, index) =>{
			console.log(room);
			return (
			<option value={room.IdArea}>{room.BranchName}</option>
			)
		  })
		}
		return result;
	  }
    // componentDidMount(){
    //     console.log('did');
    //     this.props.fetchAllProducts();
    //   }
   // console.log(this.props.history.match.params.id);
	  onChange = (object, callback)=>{
		  this.props.onChangeTable(object)
		 
		  if(callback){
			setTimeout(callback,)
		  }

	  }
  render() 
  
  {
      console.log(this.props.data);
     // console.log(this.props.products);
  
    return (
    <div>
         <div class="row filter-search margin-search">
	<div class="col-md-5 form-group">
		<input type="text" name="txtproductname" 
		value = {this.props.event.table}
		onChange = {(e)=>{
			this.onChange({type :{
				fetch_table :{
					type : 'ONCHANGE_TABLE',
					text : e.target.value
				}
			}}, ()=>{
				this.props.fetchAllTables({index : 1,
					room: this.props.event.room,
					table: this.props.event.table,
					status: this.props.event.status})
		  
									})
			// this.props.onChangTable({event : e.target.value,
			// 						type : 'ONCHANGE_TABLE'		
			//}
			//)

		}} placeholder="Nhập mã hoặc tên bàn" class="form-control size-group"/>
	</div>
	<div class="col-md-2 form-group p-0">
		<select
		value = {this.props.event.room}
		onChange = {(e)=>{
			this.onChange({type :{
				fetch_room :{
					type : 'ONCHANGE_ROOM',
					text : e.target.value
				}
			}}, ()=>{
				this.props.fetchAllTables({index : 1,
					room: this.props.event.room,
					table: this.props.event.table,
					status: this.props.event.status})
		  
									})
			// this.props.onChangTable({event : e.target.value,
			// 						type : 'ONCHANGE_TABLE'		
			//}
			//)

		}}
		 class="form-control size-group"   disabled={this.state.disabled} >
						<option value = ''>Tất cả</option>

						{this.ListRoom(this.props.room)}
		</select>
	</div>
	<div class="col-md-2 form-group ">
		<select 
		onChange = {(e)=>{
			this.onChange({type :{
				fetch_status :{
					type : 'ONCHANGE_STATUS',
					text : e.target.value
				}
			}}, ()=>{
				this.props.fetchAllTables({index : 1,
					room: this.props.event.room,
					table: this.props.event.table,
					status: this.props.event.status})
		  
									})
			// this.props.onChangTable({event : e.target.value,
			// 						type : 'ONCHANGE_TABLE'		
			//}
			//)

		}}
		value = {this.props.event.status} class="form-control size-group" disabled={this.state.disabled}>
			<option value ='' >Tất cả</option>
			{this.ListStatus(this.props.status)}

		</select>
	</div>
	<div class="col-md-3 form-group">
		<button class={this.state.search}   
		onClick = {(ev)=>{this.setState({disabled : 'disabled',
										search :'btn btn-primary size-group hide-modal',
										undo : 'btn btn-primary size-group'

		})}}>
		<i class="fa fa-search" aria-hidden="true"></i> Tìm</button>
		<button 
		class={this.state.undo}  
		onClick = {(ev)=>{this.setState({disabled : '',
										search :'btn btn-primary size-group',
										undo : 'btn btn-primary size-group hide-modal'})}}>
		<i class="fa fa-undo" aria-hidden="true"></i> Làm mới</button>

	</div>
</div> 
{/* <div class="row filter-search margin-search">
	<div class="col-xs-4 col-sm-4 col-md-4 col-lg-4 form-group">
		<input type="text" name="txtwarehousing" class="form-control size-group" placeholder="Nhập mã phiếu nhập để tìm kiếm"/>
	</div>
	<div class="col-xs-6 col-sm-6 col-md-6 col-lg-6">
		<div class="input-group flex">
			<input type="date" class="form-control size-group"/>
	        <div  className="from">
	          <span >Đến</span>
	        </div>
        	<input type="date" class="form-control size-group" />
      	</div>
	</div>
	<div class="col-xs-2 col-sm-2 col-md-2 col-lg-2 form-group">
		<button class="btn btn-primary size-button"><i class="fa fa-search" aria-hidden="true"></i> Tìm</button>
	</div>
	
</div> */}
<div class="row">
	<div class="col-xs-12 col-sm-12 col-md-12 col-lg-12 margin-table">
	
	<div class="panel panel-primary set-border">
		  <div class="panel-heading set_typecolor">
				<h3 class="panel-title">Thông Tin</h3>
		  </div>
		  <div class="panel-body">
				
				<div class="table-responsive">
					<table class="table table-hover">
						<thead>
							<tr>
								<th>STT</th>
								<th>Mã Bàn</th>
								<th>Tên bàn</th>
								<th>Trạng thái</th>
								<th>Phòng, Tầng</th>
								<th>Hóa Đơn</th>
								<th>Hành Động</th>


							</tr>
						</thead>
						<Listroom/>
					</table>
				</div>
				
		  </div>
	</div>
	
	</div>
	
	
	<ListPagination/>
</div> 
</div>
    );
  }
}

  

const  mapStateToProps = state =>{
  
	return{
	  event : state.event,
	  room : state.rooms,
	  
	  status : state.stttable

	}
  }
  
  
  const mapDispatchToProps = (dispatch, props) =>{
	  console.log('dispatch');
	return {
		fetchAllTables : (index) =>{
			dispatch(action.acFetchTableRequest(index));
		   
		  },
	  onChangeTable : (object) =>{

		dispatch(object);
		//console.log(this.props.data);
	  },
	  onSearchTable : (object,index) =>{

		dispatch(action.acSearchTablesRequest(object,index));
		//console.log(this.props.data);
	  },
	 
	  changefornavigation : (index) =>{
		dispatch(index)
	  }
	}
  }
  
  
  export default connect(mapStateToProps,mapDispatchToProps)(Table);
