 onchange = (event, type) =>{
	var target = event.target;
    var value = target.value;
    this.props.onchange({data : value,
                    type : type })
	

}
submitinsert = (data) =>{
this.props.onInsertTable(data)
}
  render() 
  
  {
      
console.log(this.props.form);
    return (
						
        <div class="modal-body">
        <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Mã Bàn</b>
        </div>
        <div class="col-md-9 format-text">
          <input type="text" 
           type="text" 
           name="id" 
           value = {this.props.form.idtable}
           onChange = {(e)=>{this.onchange(e,'CHANGE_TABLE_IDTABLE')}}
           placeholder="Nhập mã bàn" 
           class="form-control" />
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Tên Bàn</b>
        </div>
        <div class="col-md-9">
          <input 
          type="text" 
          name="name" 
          value = {this.props.form.name}
          onChange = {(e)=>{this.onchange(e,'CHANGE_TABLE_NAME')}}
          placeholder="Nhập tên bàn"
          class="form-control"/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Phòng / Tầng</b>
        </div>
        <div class="col-md-9">
    <SelectRoom/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Trạng thái</b>
        </div>
        <div class="col-md-9">
        <SelectSTT/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Hóa Đơn</b>
        </div>
        <div class="col-md-9">
          <input 
          type="text" 
          name="name" 
          value = {this.props.form.bill}
          onChange = {this.onChange}
          placeholder="Hóa Đơn"
          disabled
          class="form-control"/>
        </div>
    </div>
    
        </div>
    )
    

}
}
const  mapStateToProps = state =>{
	console.log(state.products);
  
	return{
      form : state.formtable,
      event : state.event
	}
  }
const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
   
    onUpdateTable : (id) =>{
     
      dispatch(action.acUpdateProductsRequest(id));
    },
    onchange : (text) =>{
        dispatch(text)
    },
    onInsertTable : (data) =>{
        dispatch (action.acInsertTableRequest(data))
    }
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(ModalInsert);
