import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import * as action from '../../actions/index';
import {connect} from 'react-redux';
import SelectSTT from './SelectSTT';
import SelectRoom from './SelectRoom';






class ModalInsert extends Component {
  
//  submitUpdate = (id, status) =>{
// 	this.props.callbackforsubmit(id, status);
//  }
//  shouldComponentUpdate(x,y){
// 	 console.log('updatecomponent');
// 	 return true
//  }
 onChange = (event, type) =>{
	var target = event.target;
    var value = target.value;
    this.props.onchange({data : value,
                    type : type })
	

}

  render() 
  
  {
      
console.log(this.state);
    return (
						
<div class={this.props.Ontap}  id="modal-id">
			<div class="modal-dialog margin-modal-table">
				<div class="modal-content">
					<div class="modal-header background-modal">
						<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>
						<h4 class="modal-title">Thêm Dữ Liệu</h4>
					</div>
					<div class="modal-body">
					<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Mã Bàn</b>
                    </div>
                    <div class="col-md-9 format-text">
					  <input type="text" 
					   type="text" 
					   name="id" 
					   value = {this.props.form.idtable}
					   onChange = {(e)=>{this.onchange(e,'CHANGE_TABLE_IDTABLE')}}
					   placeholder="Nhập mã bàn" 
					   class="form-control" />
                    </div>
                </div>
                <div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Tên Bàn</b>
                    </div>
                    <div class="col-md-9">
					  <input 
					  type="text" 
					  name="name" 
                      value = {this.props.form.name}
                      onChange = {(e)=>{this.onchange(e,'CHANGE_TABLE_NAME')}}
					  placeholder="Nhập tên bàn"
					  class="form-control"/>
                    </div>
                </div>
				<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Phòng / Tầng</b>
                    </div>
                    <div class="col-md-9">
				<SelectRoom/>
                    </div>
                </div>
				<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Trạng thái</b>
                    </div>
                    <div class="col-md-9">
					<SelectSTT/>
                    </div>
                </div>
				<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Hóa Đơn</b>
                    </div>
                    <div class="col-md-9">
					  <input 
					  type="text" 
					  name="name" 
					  value = {this.props.form.bill}
					  onChange = {this.onChange}
					  placeholder="Hóa Đơn"
					  disabled
					  class="form-control"/>
                    </div>
                </div>
				
					</div>
					<div class="modal-footer">
						<button type="button" class="btn btn-danger" onClick = {() =>{this.props.callbackfortap('hide-modal')}}><i class="fa fa-close" aria-hidden="true"></i>&nbsp;&nbsp;Close</button>
						<button type="button" class="btn btn-primary" onClick = {() => this.submit()}><i class="fa fa-save" aria-hidden="true"></i>&nbsp;&nbsp;Save changes</button>
					</div>
				</div>
			</div>
		</div>
       
    )
    

}
}
const  mapStateToProps = state =>{
	console.log(state.products);
  
	return{
	  form : state.formtable
	}
  }
const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
   
    onUpdateTable : (id) =>{
     
      dispatch(action.acUpdateProductsRequest(id));
    },
    onchange : (text) =>{
        dispatch(text)
    }
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(ModalInsert);
