import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import {connect} from 'react-redux';
import * as action from './../../actions/index';






class ListTable extends Component {
  
  componentWillMount(){
		this.props.fetchAllProducts();
	}
	
   // console.log(this.props.history.match.params.id);
   showRooms = (tables) =>
   {
 console.log(tables);
	 var result = null;
	 if(tables.length > 0)
	 {
	   
	   result = tables.map((table,index) =>
	   {
		console.log(index);
		return (

      <tr>
      <td>{table.IdArea}</td>
      <td>{table.BranchName}</td>
      <td>
         
         <button type="button" 
         class="btn btn-primary"  onClick = {() => this.onUpdate({id : table.id})}>Update</button>
         
         <button type="button" 
         class="btn btn-danger margin-button"
         onClick = {() => this.onDelete({id : table.id})}
         >Delete</button>
         
         

      </td>

  </tr>

			// <ListTable
			// key = {index}
			// product = {table}
			// index = {index}
			
			// />
		  );
	   })
	 }
	return result
   };
   // console.log(this.props.history.match.params.id);
 onUpdate = (id) =>{
  window.$('#modal-update').modal('show');
 }
 onDelete = (id) =>{
     this.props.onDeleteProduct(id);
 }
  render() 
  
  {
      

    return (
      <tbody>
							{this.showRooms(this.props.products)}


              <div class="modal fade" id="modal-update">
			<div class="modal-dialog">
				<div class="modal-content">
					<div class="modal-header background-modal">
						<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>
						<h4 class="modal-title">Thêm Dữ Liệu</h4>
					</div>
					<div class="modal-body">
					<div class="row form-group">
                    <div class="col-md-3">
                        <b>Mã phòng / tầng</b>
                    </div>
                    <div class="col-md-9">
					  <input type="text" 
					   type="text" 
					   name="id" 
					  //  value = {this.state.id}
					  //  onChange = {this.onChange}
					   placeholder="Nhập mã phòng / tầng" 
					   class="form-control" />
                    </div>
                </div>
                <div class="row form-group">
                    <div class="col-md-3">
                        <b>Tên phòng / tầng</b>
                    </div>
                    <div class="col-md-9">
					  <input 
					  type="text" 
					  name="name" 
					  // value = {this.state.name}
					  // onChange = {this.onChange}
					  placeholder="Nhập tên phòng / tầng"
					  class="form-control"/>
                    </div>
                </div>
					</div>
					<div class="modal-footer">
						<button type="button" class="btn btn-danger" data-dismiss="modal"><i class="fa fa-close" aria-hidden="true"></i>&nbsp;&nbsp;Close</button>
						<button type="button" class="btn btn-primary" onClick = {() => this.submit()}><i class="fa fa-save" aria-hidden="true"></i>&nbsp;&nbsp;Save changes</button>
					</div>
				</div>
			</div>
		</div>
				</tbody>
       
    )
    

}
}
const  mapStateToProps = state =>{
  console.log(state.products);

  return{
    products: state.products
  }
}


const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
    fetchAllProducts : () =>{
      dispatch(action.acFetchProductsRequest());
    },
    onDeleteProduct : (id) =>{
     
      dispatch(action.acDeleteProductsRequest(id));
    }
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(ListTable);
