// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';






class ListText extends Component {
  


 
  
  render() 
  
  {
      
console.log('da vao listbutton');
var {Button,index} = this.props;
    return (
        <td>
         
        <button type="button" 
        class="btn btn-primary"  onClick = {() => this.onUpdate({id : Button.id})}>Update</button>
        
        <button type="button" 
        class="btn btn-danger margin-button"
        onClick = {() => this.onDelete({id : Button.id})}
        >Delete</button>
        
        

     </td>

            
		
       
    )
    

}
}

export default ListText;
