class Room extends Component {
	constructor(props){
		super(props);
		this.state = {
			name : '',
			
		}
	  }
	  onChange = (event) =>{
		var target = event.target;
		var value = target.value;
		this.setState({
			name : value
		},this.callbackFunction)
		
   
	}
	callbackFunction = () => 
	{ 
		console.log('new state', this.state.name) 
	}
