import * as Types from './../../constants/ActionType';

const initialState = [{}];


const products = (state = initialState , action) =>
{

  let newState = [...state];
  if(typeof action.type.fetch_product === "undefined")
  {
    return newState
  }
  else{
    switch(action.type.fetch_product.type){ 
      case Types.FETCH_PRODUCTS:
    
        newState = action.type.fetch_product.data;
        return newState;        
        default: 
       return newState;
    }
  }
  
}
export default products;
