import React, { Component } from 'react';
import DashBoard from './pages/DashBoard/DashBoard';
import routes from './routes'
import { Switch, Route , Link ,BrowserRouter as Router, IndexRoute } from 'react-router-dom';
import Menu from './components/Menu/Menu';


class App extends Component {
  showContentMenus = (routes) => {
    var result = null;
    if(routes.length > 0){


      result = routes.map((route, index)=>{

        if(route.path === '/warehousing'){
          return (
            <Route
            key = {index}

            path = {route.path}
            exact = {route.exact}
            component = {route.main}
          />
          )

        }

       else if (route.path === '/Menu'){

          return (
            <Route
              key = {index}

              path = '/Menu'
              exact = {false} 
              component = {Menu}
            >
               <Route

               exact = {route.exact}
                path={route.path} 

               component={route.main} />

              </Route>
          )




        }
       else{
         return null;
       }


      })
    }

  return <Switch>{result}</Switch>
  }
  render() {
    return (

    <Router>
          <div>



          <div class="container">

          <div class="row">
            {this.showContentMenus(routes)}
          </div>

        </div>

        </div>
      </Router>

    );
  }
}

export default App;
