   // console.log(this.props.history.match.params.id);
 
 onDelete = (id) =>{
     this.props.onDeleteProduct(id);
 }
  render() 
  
  {
      var {data, index} = this.props
      console.log('da vao listdata');
      console.log(this.props);

    return (
        <tr>
          <td>{index}</td>
        <td>{data.IdArea}</td>
        <td>{data.BranchName}</td>
        <td>
            
             
            <button type="button" 
            class="btn btn-primary"  onClick = {() =>{this.props.OntapRoom(
              {type : {
                mroom_ontap : {
                    type : 'MODAL_HEADER_FOR_ONTAP_ROOM',
                    event : 'show-modal',
                      }, 
                mroom_text :{
                    type : 'MODAL_HEADER_FOR_INSERT_UPDATE_ROOM',
                    event : 'Chỉnh sửa dữ liệu'}
                  }
        
          })}}><i class="fa fa-edit"></i>&nbsp;&nbsp;Update</button>
            
            <button type="button" 
            class="btn btn-danger margin-button"
            onClick = {() => this.onDelete({id : data.id})}
            ><i class="fa fa-trash" aria-hidden="true"></i>&nbsp;&nbsp;Delete</button>
            
            
    
         
        </td>
    
      </tr>
       
    )
    

}
}
const  mapStateToProps = state =>{
  console.log('vao map trong listdata');

  return{
    products: state.products
  }
}
const mapDispatchToProps = (dispatch, props) =>{
  console.log('dispatch');
return {
  
  onDeleteProduct : (id) =>{
   
    dispatch(action.acDeleteProductsRequest(id));
  },
  OntapRoom : (tap) =>{
		dispatch(tap)
	},

}
}


export default connect(null,mapDispatchToProps)(ListData);
