    console.log(result);
    return result;
}

    return result;


     

    };


  render() 
  
  {
     var x = this.showPagination(2);
   console.log(x);
    return (
        <div class="col-xs-12 col-sm-12 col-md-12 col-lg-12">
		
		<ul class="pagination pagination-lg float-pagination">
			<li><a href="#">&laquo;</a></li>
            {this.showPagination(this.props.tables)}
			{/* <li><a href="#">1</a></li>
