import React  from 'react';
import Customer from './pages/Customer/Customer';
import Order from './pages/Order/Order';
import Pos from './pages/Pos/Pos';
import Product from './pages/Product/Product';
import Table from './pages/Table/Table';
import WareHousing from './pages/WareHousing/WareHousing';
import DashBoard from './pages/DashBoard/DashBoard';
import { createStore, applyMiddleware} from 'redux';
import appReducers from './reducers/Dashboard/index';
import thunk from 'redux-thunk';

import { Provider } from 'react-redux';

function Create_Store(storeName, Component)
{
   
    storeName = createStore(
        appReducers,  

        applyMiddleware(thunk) 
    ) 
    return ( <Provider store = {storeName}>
               <Component/>
            </Provider>
    )

}


const routes = [

    {
        path : '/home' ,
          exact : true,
          main : () => {return Create_Store("storeDashBoard" , DashBoard)}
       },
  
  
   {
    path : '/customer' ,
    exact : false,
    main : () => <Customer/>
    },
    {
        path : '/order' ,
        exact : false,
        main : () => <Order  />
    } ,
    {
        path : '/pos' ,
        exact : false,
        main : () => <Pos/>
    },
    {
        path : '/product' ,
        exact : false,
        main : () => <Product/>
    },
    {
        path : '/table' ,
        exact : false,
        main : () => <Table/>
    },
    {
        path : '/warehousing' ,
        exact : false,
        main : () => <WareHousing/>
    },

];
export default routes;
