import * as Types from './../constants/ActionType';
import callApi from './../ultis/apiCaller';
export const acFetchProductsRequest = () => {
   
    return next =>{
       
         callApi('getdata', 'GET', null).then(res =>{
             console.log(res);
           
           next(acFetchProducts(res.data));
          
        });

    };
}
export const acInsertProductsRequest = (data) => {

    return next =>{
       
         callApi('insertdata', 'POST', {data}).then(res =>{
           console.log(res);
           next(acFetchProducts(res.data));
          
        }).catch((err) =>{console.log(err)});

    };
}
export const acInsertProducts = (product) =>{
    
    return {
        type : Types.INSERT_PRODUCT,
        product
    }
}
export const acFetchProducts = (product) =>{
    
    return {
        type : Types.FETCH_PRODUCTS,
        product
    }
}
export const acDeleteProductsRequest = (id) => {
   
    return next =>{
       
         callApi(`product/${id}`, 'DELETE', null).then(res =>{
             
           
           next(actDeleteProduct(id));
          
        });

    };
}
export const actDeleteProduct = (id) =>{
    return {
        type : Types.DELETE_PRODUCT,
        id
    }
}
