import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import * as action from '../../actions/index';
import {connect} from 'react-redux';
import Modal from './ModTable';

import Emty from './Emty';


class ListTable extends Component {
  

  // shouldComponentUpdate(nextProps, nextState){
  //   return true
  // }

  constructor(props){
    super(props);
    this.state = {
      id : '',
      idarea : '',
      name : '',
      onTap : 'hide-modal',

    }
    } 
    componentWillMount(){
      console.log('willmountnha');
      this.props.fetchAllTables({index : 1}
     );
    }
    showData = (datas,id) =>
    {
        console.log(id);
    var result = null;
    if(datas.length > 0)
    {
      
      result = datas.map((data,index) =>
      {
        var statusName = (data.idStatus == 1) ? 'Đang sử dụng'  : 'Trống';
        var statusClass = (data.idStatus == 1) ? 'success' : 'default';
     return (
 
      <tr>
      <td>{((id-1) * 5) + index+1}</td>
    <td>{data.IdTable}</td>
    <td>{data.TableName}</td>
    <td>
             
             <span class={`size label label-${statusClass}`}>
                {statusName}
             </span>
             
           </td>
    {/* <td>{data.idStatus}</td> */}
    <td>{data.IdArea}</td>
    <td>{data.idBill}</td>



    <td>
        
         
        <button type="button" 
        class="btn btn-primary"  onClick = {() => this.onUpdate(data)}><i class="fa fa-edit"></i>&nbsp;&nbsp;Update</button>
        
        <button type="button" 
        class="btn btn-danger margin-button"
        onClick = {() => this.onDelete({id : data.id})}
        ><i class="fa fa-trash" aria-hidden="true"></i>&nbsp;&nbsp;Delete</button>
        
        

     
    </td>

  </tr>
      //  <ListDataofRoom
      //  index = {index}
      //  id = {((id-1) * 5) + index+1}
      //  data = {data}
       
      //  >

      //  </ListDataofRoom>
 
      
       );
      })
    }
    else
    {
      return (
        <Emty/>
      )
    }
   return result
    };
  callbackFunction = (data) => 
  {
  
    this.setState({
      id : data.id,
      idarea : data.IdArea,
      name : data.BranchName,
      onTap : 'modal fade show-modal'
    })
  }
  callbackfunctionforSubmitupdate = (id, status) =>{
    this.setState({onTap : status}, ()=>{
      this.props.onUpdateTable(id)
    })

  }
  callbackfunctionForonChange = (name, value) =>{
   
    this.setState({
      [name] : value
    })
 
  }
 
   // console.log(this.props.history.match.params.id);
  
   // console.log(this.props.history.match.params.id);
 
//  onDelete = (id) =>{
//      this.props.onDeleteProduct(id);
//  }
onDelete = () =>{
  console.log('listtable');
  this.setState({});
}
  render() 
  
  {
      console.log('da vao listtable')
   console.log(this.props.tables);
    return (
     <tbody>
						{this.showData(this.props.table.data, this.props.table.index)}




            <Modal 
            
             state = {this.state}
            //  callbackOnchange = {this.callbackfunctionForonChange}
            //  callbackforsubmit = {this.callbackfunctionforSubmitupdate}
             />
            
             </tbody>
       
    )
    

}
}



const  mapStateToProps = state =>{
  

  return{
   table : state.table
  }
};


const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
    fetchAllTables : (index) =>{
      dispatch(action.acFetchTableRequest(index));
     
    },
    // onDeleteProduct : (id) =>{
     
    //   dispatch(action.acDeleteProductsRequest(id));
    // },
    // onUpdateTable : (id) =>{
     
    //   dispatch(action.acUpdateProductsRequest(id));
    // }
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(ListTable);
