import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import {connect} from 'react-redux';
import * as action from './../../actions/index';
import Modal from './Modal'





class ListTable extends Component {
  

  shouldComponentUpdate(nextProps, nextState){
    console.log(JSON.stringify(nextProps.products));
    console.log(JSON.stringify(this.props.products));
    return true
  }

  constructor(props){
    super(props);
    this.state = {
      id : '',
      name : '',
    }
    } 

  componentWillMount(){
		this.props.fetchAllProducts();
	}
	
   // console.log(this.props.history.match.params.id);
   showRooms = (tables) =>
   {
 console.log(tables);
	 var result = null;
	 if(tables.length > 0)
	 {
	   
	   result = tables.map((table,index) =>
	   {
		console.log(index);
		return (

      <tr>
      <td>{table.IdArea}</td>
      <td>{table.BranchName}</td>
      <td>
         
         <button type="button" 
         class="btn btn-primary"  onClick = {() => this.onUpdate({id : table.id})}>Update</button>
         
         <button type="button" 
         class="btn btn-danger margin-button"
         onClick = {() => this.onDelete({id : table.id})}
         >Delete</button>
         
         

      </td>

  </tr>

			// <ListTable
			// key = {index}
			// product = {table}
			// index = {index}
			
			// />
		  );
	   })
	 }
	return result
   };
   // console.log(this.props.history.match.params.id);
 onUpdate = (id) =>{
  window.$('#modal-update').modal('show');
  this.setState({
    id : id
  })
 }
 onDelete = (id) =>{
     this.props.onDeleteProduct(id);
 }
  render() 
  
  {
      
console.log('da vao listtable');
    return (
      <tbody>
							{this.showRooms(this.props.products)}


             <Modal state = {this.state}/>
		
				</tbody>
       
    )
    

}
}
const  mapStateToProps = state =>{
  console.log(state.products);

  return{
    products: state.products
  }
}


const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
    fetchAllProducts : () =>{
      dispatch(action.acFetchProductsRequest());
    },
    onDeleteProduct : (id) =>{
     
      dispatch(action.acDeleteProductsRequest(id));
    }
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(ListTable);
