import * as Types from './../../constants/ActionType';

const initialState = [{}];
var x = initialState;

const products = (sta = initialState , action, x) =>
{
  console.log(x)
  console.log(sta);

  let newState = [...sta];
  console.log(newState);

  console.log('vao dispath product');
  console.log(action)
  switch(action.type){ 
    case Types.FETCH_PRODUCTS:
    case Types.INSERT_PRODUCT:  
    case Types.DELETE_PRODUCT :
    case Types.UPDATE_PRODUCT :  
    case Types.SEARCH_PRODUCT :
      newState = action.product;
      return newState;        
      break;  
    default: 
     return newState;
  }
}
export default products;
