import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import {connect} from 'react-redux';
import * as action from './../../actions/index';
import Modal from './Modal'
import ListText from './ListText';
import ListTextName from './ListTextName';

import ListButton from './ListButton';




class ListTable extends Component {
  

  // shouldComponentUpdate(nextProps, nextState){
  //   return true
  // }

  constructor(props){
    super(props);
    this.state = {
      id : '',
      name : '',
    }
    } 

  componentWillMount(){
		this.props.fetchAllProducts();
	}
	
   // console.log(this.props.history.match.params.id);
   showRooms = (tables) =>
   {
 console.log(tables);
	 var result = null;
	 if(tables.length > 0)
	 {
	   
	   result = tables.map((table,index) =>
	   {
		console.log(index);
		return (

      <tr>
      <ListText
      key = {index}
      Text = {table}
      />
       <ListTextName
      key = {index}
      TextName = {table}
      />
        <ListButton
         key = {index}
         Text = {table}
        />

  </tr>

			// <ListTable
			// key = {index}
			// product = {table}
			// index = {index}
			
			// />
		  );
	   })
	 }
	return result
   };
   // console.log(this.props.history.match.params.id);
 onUpdate = (id) =>{
  window.$('#modal-update').modal('show');
  this.setState({
    id : this.state.id
  })
 }
 onDelete = (id) =>{
     this.props.onDeleteProduct(id);
 }
  render() 
  
  {
      

    return (
      <tbody>
							{this.showRooms(this.props.products)}


             <Modal state = {this.state}/>
		
				</tbody>
       
    )
    

}
}
const  mapStateToProps = state =>{
  console.log(state.products);

  return{
    products: state.products
  }
}


const mapDispatchToProps = (dispatch, props) =>{
    console.log('dispatch');
  return {
    fetchAllProducts : () =>{
      dispatch(action.acFetchProductsRequest());
    },
    onDeleteProduct : (id) =>{
     
      dispatch(action.acDeleteProductsRequest(id));
    }
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(ListTable);
