import React, { Component } from 'react';
import { connect } from 'react-redux';

import Room from './Room';
import Table from './Table';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';





///////







///////////
class Tabs extends Component {
  constructor(props){
    super(props);
    this.state = {
      table :{
        display : 'show-alert',
        action : 'active',
        textcolor : 'colorforontap',
      },
      room :{
        display : 'hide-modal',
        action : '',
        textcolor : 'default-color',
      }
    }
    }
   // console.log(this.props.history.match.params.id);
  onTapTable = (name) =>{
    var statefornewUpdate =  {};

    if(name == 'table'){
    //  statefornewUpdate = {
    //   table :{
    //     display : 'show-alert',
    //     action : 'active',
    //     textcolor : 'colorforontap',
    //   },
    //   room :{
    //     display : 'hide-modal',
    //     action : '',
    //     textcolor : 'default-color',
    //   }
      
    // }     
      this.props.onChangeTab({type : {
        ontapRoom :{
          type : 'ONTAP_CHANGE_ROOM',
          ontap : {
            display : 'hide-modal',
            action : '',
            textcolor : 'default-color',
          }
        },
        ontapTable :{
          type : 'ONTAP_CHANGE_TABLE',
          ontap : {
            display : 'show-alert',
            action : 'active',
            textcolor : 'colorforontap',
          },
        }
      }})
    }
    else
    {
      this.props.onChangeTab({type : {
        ontapRoom :{
          type : 'ONTAP_CHANGE_ROOM',
          ontap : {
            display : 'show-alert',
            action : 'active',
            textcolor : 'colorforontap',
          },
        },
        ontapTable :{
          type : 'ONTAP_CHANGE_TABLE',
          ontap : {
            display : 'hide-modal',
            action : '',
            textcolor : 'default-color',
          }
        },
      }})
    }
  }
  render() 
  
  {
    console.log('da vao tabs')
    console.log(this.state);

    return (
     
       
        
			<div class="col-md-12 " id="table-list">
			
      <div role="tabpanel">
          <ul class="nav nav-tabs" role="tablist">
              <li role="presentation" className={this.props.ontap.table.action} onClick = {()=>{this.onTapTable('table')}}>
                  <a href="#"  onClick={ev => {ev.preventDefault()}} className={this.props.ontap.table.textcolor}>
                  <i class="fa fa-table" aria-hidden="true"></i> &nbsp;&nbsp; Bàn</a>
              </li>
              <li role="presentation"  className={this.props.ontap.room.action} onClick = {()=>{this.onTapTable('room')}}>
                  <a href="#" onClick={ev => {ev.preventDefault()}} className={this.props.ontap.room.textcolor}>
                  <i class="fa fa-home" aria-hidden="true"></i> &nbsp;&nbsp;
                    Phòng / Tầng</a>
              </li>
          </ul>
      
          <div class="tab-content">
              <div role="tabpanel" class="tab-pane " id={this.props.ontap.table.display}>
                  <Table/>
              </div>
              <div role="tabpanel" class="tab-pane" id={this.props.ontap.room.display}>
              <Room/>
              </div>
          </div>
      </div>
      
			</div>

        

    );
  }
}

const  mapStateToProps = state =>{
  console.log('map')

  return{
  ontap : state.onTap
  }
}

const mapDispatchToProps = (dispatch, props) =>{
return {
  onChangeTab : (tap) =>{
    dispatch(tap)
  }
}
}


export default connect(mapStateToProps,mapStateToProps)(Tabs);
