import React, { Component } from 'react';

import { connect } from 'react-redux';
// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';

import {acFetchProductsRequest, acDeleteProductsRequest} from './../../actions/index';
import ListTable from './ListTable'







class Room extends Component {
  
	componentWillMount(){
		this.props.fetchAllProducts();
	}
	
   // console.log(this.props.history.match.params.id);
   showRooms = (tables) =>
   {
 console.log(tables);
	 var result = null;
	 if(tables.length > 0)
	 {
	   
	   result = tables.map((table,index) =>
	   {
		console.log(index);
		return (
			<ListTable
			key = {index}
			product = {table}
			index = {index}
			
			/>
		  );
	   })
	 }
	return result
   };
  render() 
  {
    console.log("da vao room");

    return (
    <div>
         <div class="row filter-search margin-search">
	<div class="col-md-5 form-group">
		<input type="text" name="txtproductname" placeholder="Nhập mã hoặc tên bàn" class="form-control size-group"/>
	</div>

	<div class="col-md-3 form-group">
		<button class="btn btn-primary size-group disabled " disabled><i class="fa fa-search" aria-hidden="true"></i> Tìm</button>
	</div>
	
</div> 
