<div class="row">
	<div class="col-xs-12 col-sm-12 col-md-12 col-lg-12 margin-table">
	
	<div class="panel panel-primary set-border">
		  <div class="panel-heading set_typecolor">
				<h3 class="panel-title">Thông Tin</h3>
		  </div>
		  <div class="panel-body">
				
				<div class="table-responsive">
					<table class="table table-bordered table-hover">
						<thead>
							<tr>
								<th>Mã Phòng / Tầng</th>
                                <th>Tên Phòng / Tầng</th>
								<th>Hành Động</th>

							</tr>
						</thead>

							<tbody>
								{this.showRooms(
								this.props.products
								)}
							</tbody>
					
					</table>
				</div>
				
		  </div>
	</div>
	
	</div>
	
	
	
</div> 
</div>
    );
  }
}


const  mapStateToProps = state =>{
    console.log(state.products);
  
    return{
      products: state.products
    }
  }
  const mapDispatchToProps = (dispatch, props) =>{
      console.log('dispatch');
    return {
      fetchAllProducts : () =>{
        dispatch(acFetchProductsRequest());
      },
      onDeleteProduct : (id) =>{
        dispatch(acDeleteProductsRequest(id));
      }
    }
  }
  

export default connect(mapStateToProps,mapDispatchToProps)(Room);
