  render() 
  
  {
      
var {Text,index} = this.props;
    return (
       <div class="modal-body">
					<div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Mã phòng / tầng</b>
                    </div>
                    <div class="col-md-9">
					  <input type="text" 
					   type="text" 
					   name="id" 
					   value = {this.state.id}
					   onChange = {this.onChange}
					   placeholder="Nhập mã phòng / tầng" 
					   class="form-control" />
                    </div>
                </div>
                <div class="row form-group">
                    <div class="col-md-3 format-text">
                        <b>Tên phòng / tầng</b>
                    </div>
                    <div class="col-md-9 ">
					  <input 
					  type="text" 
					  name="name" 
					  value = {this.state.name}
					  onChange = {this.onChange}
					  placeholder="Nhập tên phòng / tầng"
					  class="form-control"/>
                    </div>
                </div>
					</div> 
    )
    

}
}

export default modalbody;
