import React, { Component } from 'react';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';
import * as action from '../../actions/index';
import {connect} from 'react-redux';
import SelectSTT from './SelectSTT';
import SelectRoom from './SelectCustomer';






class ModalInsert extends Component {
  
//     shouldComponentUpdate(nextProps, nextState)
// 	{
//    if((JSON.stringify(nextProps.form) == JSON.stringify(this.props.form)) )
// 		{
//       return false;
//     }
//     return true;
// 	}

 onchange = (event, object) =>{
    this.props.onchange({type : object})
	

}
submitinsert = (data) =>{
this.props.onInsertTable(data)
}
  render() 
  
  {
      
    return (
						
        <div class="modal-body">
        <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Mã Nhà Cung Cấp</b>
        </div>
        <div class="col-md-9 format-text">
          <input type="text" 
           type="text" 
           name="id" 
           value = {this.props.form.idtable}
           onChange = {(e)=>{this.onchange(e,{onchangetable_idtable :{
               type : 'CHANGE_TABLE_IDTABLE',
               text : e.target.value
           }})}}
           placeholder="Mã Nhà Cung Cấp" 
           class="form-control" />
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Tên Nhà Cung Cấp</b>
        </div>
        <div class="col-md-9">
          <input 
          type="text" 
          name="name" 
          value = {this.props.form.name}
          onChange = {(e)=>{this.onchange(e,{onchangetable_name :{
            type : 'CHANGE_TABLE_NAME',
            text : e.target.value
        }})}}
          placeholder="Tên Nhà Cung Cấp"
          class="form-control"/>
        </div>
    </div>
    {/* <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Phòng / Tầng</b>
        </div>
        <div class="col-md-9">
    <SelectRoom/>
        </div>
    </div> */}
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Email</b>
        </div>
        <div class="col-md-9">
          <input 
          type="email" 
          name="name" 
          value = {this.props.form.bill}
          onChange = {this.onChange}
          placeholder="Email"
            
          class="form-control"/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Số Điện Thoại</b>
        </div>
        <div class="col-md-9">
          <input 
          type="number" 
          name="name" 
          value = {this.props.form.bill}
          onChange = {this.onChange}
          placeholder="Số Điện Thoại"
            
          class="form-control"/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Địa Chỉ</b>
        </div>
        <div class="col-md-9">
          <input 
          type="text" 
          name="name" 
          value = {this.props.form.bill}
          onChange = {this.onChange}
          placeholder="Địa Chỉ"
            
          class="form-control"/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Chú Thích </b>
        </div>
        <div class="col-md-9">
          <input 
          type="text" 
          name="name" 
          value = {this.props.form.bill}
          onChange = {this.onChange}
          placeholder="Chú Thích"
            
          class="form-control"/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Ghi Nợ</b>
        </div>
        <div class="col-md-9">
          <input 
          type="text" 
          name="name" 
          value = {this.props.form.bill}
          onChange = {this.onChange}
          placeholder="Ghi Nợ"
            
          class="form-control"/>
        </div>
    </div>
    <div class="row form-group">
        <div class="col-md-3 format-text">
            <b>Ảnh Đai Diện</b>
        </div>
        <div class="col-md-09 text-center">
                        <div class="jumbotron">
                            <h3>Upload hình ảnh nhà cung cấp</h3>
                            <p>(Để tải và hiện thị nhanh, mỗi ảnh lên có dung lượng tối đa 5MB.)</p>
                            <input type="file" class="form-control-file" id="exampleFormControlFile1"/>  
                        </div>
                    </div>
    </div>
    
        </div>
    )
    

}
}
const  mapStateToProps = state =>{
  
	return{
      form : state.formtable,
      event : state.event
	}
  }
const mapDispatchToProps = (dispatch, props) =>{
  return {
   
   
    onchange : (text) =>{
        dispatch(text)
    },
    onInsertTable : (data) =>{
        dispatch (action.acInsertTableRequest(data))
    }
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(ModalInsert);
