import * as Types from './../../constants/ActionType';
import callApi from './../../ultis/apiCaller';

const initialState = () =>
{
  
  return callApi('getdata', 'GET', null).then(res =>{
    return res.data;
});
  
}


export default function (state , action)
{
  console.log(state);
  let newState = [...state];
  switch(action.type){ 
    case Types.INSERT_PRODUCT :
        newState = action.product;
        return newState;    
      default: return newState;
  }
}
