import React, { Component } from 'react';

import Room from './Room';
import Table from './Table';

// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';





///////







///////////
class Tabs extends Component {
  constructor(props){
    super(props);
    this.state = {
      table :{
        display : 'show-alert',
        action : 'active',
        textcolor : 'colorforontap',
      },
      room :{
        display : 'hide-modal',
        action : '',
        textcolor : 'default-color',
      }
    }
    }
   // console.log(this.props.history.match.params.id);
  onTapTable = (name) =>{
    var statefornewUpdate =  {};

    if(name == 'table'){
     statefornewUpdate = {
      table :{
        display : 'show-alert',
        action : 'active',
        textcolor : 'colorforontap',
      },
      room :{
        display : 'hide-modal',
        action : '',
        textcolor : 'default-color',
      }
      
    }     
      this.setState({table : {...statefornewUpdate.table},room :  {...statefornewUpdate.room}})
    }
    else
    {
      console.log('day la room nha')
      statefornewUpdate = {
        table :{
          display : 'hide-modal',
          action : '',
          textcolor : 'default-color',
        },
        room :{
          display : 'show-alert',
          action : 'active',
          textcolor : 'colorforontap',
        }
      }     
        this.setState({table : {...statefornewUpdate.table},room :  {...statefornewUpdate.room}})
    }
  }
  render() 
  
  {
    console.log('da vao tabs')
    console.log(this.state);

    return (
     
       
        
			<div class="col-md-12 " id="table-list">
			
      <div role="tabpanel">
          <ul class="nav nav-tabs" role="tablist">
              <li role="presentation" className={this.state.table.action} onClick = {()=>{this.onTapTable('table')}}>
                  <a href="/#"  role="tab" data-toggle="tab" className={this.state.table.textcolor}>
                  <i class="fa fa-table" aria-hidden="true"></i> &nbsp;&nbsp; Bàn</a>
              </li>
              <li role="presentation"  className={this.state.room.action} onClick = {()=>{this.onTapTable('room')}}>
                  <a href="/#" aria-controls="tab" role="tab" data-toggle="tab" className={this.state.room.textcolor}>
                  <i class="fa fa-home" aria-hidden="true"></i> &nbsp;&nbsp;
                    Phòng / Tầng</a>
              </li>
          </ul>
      
          <div class="tab-content">
              <div role="tabpanel" class="tab-pane " id={this.state.table.display}>
                  <Table/>
              </div>
              <div role="tabpanel" class="tab-pane" id={this.state.room.display}>
              <Room/>
              </div>
          </div>
      </div>
      
			</div>

        

    );
  }
}

export default Tabs;
