import React, { Component } from 'react';
import {connect} from 'react-redux';
// import './../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import './../../../node_modules/bootstrap/dist/js/bootstrap.min';

import * as action from '../../actions/index';






class Modal extends Component {
  
 
  render() 
  
  {
      console.log('da vao modal');
console.log(this.props);
    return (
						


              <div class="modal fade" id="modal-update">
			<div class="modal-dialog">
				<div class="modal-content">
					<div class="modal-header background-modal">
						<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>
						<h4 class="modal-title">Cập Nhật Dữ Liệu</h4>
					</div>
					<div class="modal-body">
					<div class="row form-group">
                    <div class="col-md-3">
                        <b>Mã phòng / tầng</b>
                    </div>
                    <div class="col-md-9">
					  <input type="text" 
					   type="text" 
					   name="id" 
					  //  value = {this.state.id}
					  //  onChange = {this.onChange}
					   placeholder="Nhập mã phòng / tầng" 
					   class="form-control" />
                    </div>
                </div>
                <div class="row form-group">
                    <div class="col-md-3">
                        <b>Tên phòng / tầng</b>
                    </div>
                    <div class="col-md-9">
					  <input 
					  type="text" 
					  name="name" 
					  // value = {this.state.name}
					  // onChange = {this.onChange}
					  placeholder="Nhập tên phòng / tầng"
					  class="form-control"/>
                    </div>
                </div>
					</div>
					<div class="modal-footer">
						<button type="button" class="btn btn-danger" data-dismiss="modal"><i class="fa fa-close" aria-hidden="true"></i>&nbsp;&nbsp;Close</button>
						<button type="button" class="btn btn-primary" onClick = {() => this.submit()}><i class="fa fa-save" aria-hidden="true"></i>&nbsp;&nbsp;Save changes</button>
					</div>
				</div>
			</div>
		</div>
       
    )
    

}
}
const  mapStateToProps = state =>{
	console.log(state.products);
  
	return{
	  products: state.products
	}
  }
  
  
  const mapDispatchToProps = (dispatch, props) =>{
	  console.log('dispatch');
	return {
	  fetchAllProducts : () =>{
		dispatch(action.acFetchProductsRequest());
	  },
	  onDeleteProduct : (id) =>{
	   
		dispatch(action.acDeleteProductsRequest(id));
	  }
	}
  }
export default  connect(null,mapDispatchToProps)(Modal);
